CREATE TABLE "public"."products"("id" uuid NOT NULL, "name" text NOT NULL, "description" text NOT NULL, "price" money NOT NULL, "flexible_price" boolean NOT NULL, "account_access" boolean NOT NULL, "recurring" boolean NOT NULL, "recurring_period" integer NOT NULL, "created" timestamptz NOT NULL DEFAULT now(), "created_by" uuid NOT NULL, "account_id" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("account_id") REFERENCES "public"."accounts"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("created_by") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict);
