CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."events_playlists"("id" uuid DEFAULT gen_random_uuid(), "event_id" uuid NOT NULL, "playlist_id" uuid NOT NULL, "created" timestamptz NOT NULL, "created_by" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("event_id") REFERENCES "public"."events"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("playlist_id") REFERENCES "public"."playlists"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("created_by") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict);
