CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."accounts_affiliates"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "account_id" uuid NOT NULL, "user_id" uuid NOT NULL, "created_by" uuid NOT NULL, "created" timestamptz NOT NULL DEFAULT now(), "approved" boolean NOT NULL DEFAULT false, "commission" integer NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("account_id") REFERENCES "public"."accounts"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("created_by") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict);
